#rename sequences with file name to add plant species and reference genome to fasta header, then concatenate all sequences in one file "All_predicted_proteins.fasta"
cd $HOME
find gemoma/ -name '*_predicted_proteins.fasta' -exec awk '/>/{sub(">",">"FILENAME"_");sub(/\.fasta/,x);sub(/gemoma\/.*\//,x)}1' {} + > All_predicted_proteins.fasta
mv All_predicted_proteins.fasta ~/gemoma

cd $HOME/gemoma

#Divide sequences by gene and reference genome
#Os reference
#str2
grep -A 1 "ref-gene=Os07t0191600-04.gene" All_predicted_proteins.fasta > str2_Os_predicted_proteins.fasta
#symrk
grep -A 1 "ref-gene=Os07t0568100-01.gene" All_predicted_proteins.fasta > symrk_Os_predicted_proteins.fasta
#ccamk
grep -A 1 "ref-gene=Os05t0489900-01.gene" All_predicted_proteins.fasta > ccamk_Os_predicted_proteins.fasta
#cyclops
grep -A 1 "ref-gene=Os06t0115600-01.gene" All_predicted_proteins.fasta > cyclops_Os_predicted_proteins.fasta

#Mt reference
#str2
grep -A 1 "ref-gene=gene-LOC11407838" All_predicted_proteins.fasta > str2_Mt_predicted_proteins.fasta
#symrk
grep -A 1 "ref-gene=gene-LOC11407326" All_predicted_proteins.fasta > symrk_Mt_predicted_proteins.fasta
#ccamk
grep -A 1 "ref-gene=gene-CCAMK" All_predicted_proteins.fasta > ccamk_Mt_predicted_proteins.fasta
#cyclops
grep -A 1 "ref-gene=gene-IPD3" All_predicted_proteins.fasta > cyclops_Mt_predicted_proteins.fasta

#Clean headers
awk '/^>/ {print $1;next}1' str2_Os_predicted_proteins.fasta > clean_str2_Os_predicted_proteins.fasta
awk '/^>/ {print $1;next}1' symrk_Os_predicted_proteins.fasta > clean_symrk_Os_predicted_proteins.fasta
awk '/^>/ {print $1;next}1' ccamk_Os_predicted_proteins.fasta > clean_ccamk_Os_predicted_proteins.fasta
awk '/^>/ {print $1;next}1' cyclops_Os_predicted_proteins.fasta > clean_cyclops_Os_predicted_proteins.fasta

