conda activate gemoma
cd gemoma/Mt_Geranium_maculatum/
#extract matching sequences (high score) starting with M but NOT finishig with a stop codon 
GeMoMa GAF g=unfiltered_predictions_from_species_0.gff f="start=='N' and (isNaN(score) or score/aa>=0.75)"
